import React, { useState, useEffect } from "react";
import queryString from "query-string";
import io from "socket.io-client";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import Container from "@material-ui/core/Container";
import Paper from "@material-ui/core/Paper";
import { useTimer } from "use-timer";
import SpacingGrid from "./playerlist.jsx";
import { withRouter } from "react-router-dom";
import { createMuiTheme, ThemeProvider } from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";
import Popover from 'react-bootstrap/Popover'
import OverlayTrigger from 'react-bootstrap/OverlayTrigger'
import "./lobby.css";

const theme = createMuiTheme();

theme.typography.h3 = {
  fontSize: "1.2rem",
  "@media (min-width:600px)": {
    fontSize: "1.5rem",
  },
  [theme.breakpoints.up("md")]: {
    fontSize: "2rem",
  },
};

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(5),
    [theme.breakpoints.down("xs")]: {
      height: 20,
      width: 5,
      padding: theme.spacing(4),
      textAlign: "center",
    },
    height: 120,
    width: 100,
    textAlign: "center",
  },
  control: {
    padding: theme.spacing(4),
  },
}));

let scores = [];

const Lobby = (props) => {
  let { Time } = queryString.parse(window.location.search);
  //DECLARATIONS OF STATE NECESSARY THINGS
  const { time, start, pause, reset, isRunning } = useTimer({
    initialTime: 10,
    timerType: "DECREMENTAL",
  });
  const [name, setName] = useState("");
  const [room, setRoom] = useState("");
  const [currentSocket, setCurrentSocket] = useState(null);
  const [players, setPlayers] = useState([]);
  const [count, setCount] = useState(0);
  const [show, setShow] = useState(true);
  const [host, setHost] = useState("");
  const [grid, setGrid] = useState([]);
  const [gridshow, setGShow] = useState(false);
  const [endgame, setEndgame] = useState(false);
  const [words, setWords] = useState("");
  const ENDPOINT = `http://localhost:9000/rooms`;
  const classes = useStyles();
  /////

  //Use Effect renders
  useEffect(() => {
    let { name, room } = queryString.parse(window.location.search);
    console.log(queryString.parse(window.location.search));
    setName(name);
    setRoom(room);

    const socket = io(`${ENDPOINT}/${room}`);
    setCurrentSocket(socket);

    socket.emit("add_player", name);

    socket.on("add_to_list", (players_list) => {
      console.log(players_list);
      setPlayers(players_list);
    });

    socket.on("host", (host) => {
      console.log("getting called");
      console.log(host);
      setHost(host);
    });

    socket.on("starting_game", (data) => {
      console.log(data.grid[0]);
      setGrid(data.grid);
      setGShow(data.start);
      console.log(gridshow);
      if (data.start) {
        reset();
        start();
        setCount(0);
      }
    });

    socket.on("sending scores", (data) => {
      //console.log('I am getting called')
      console.log(data.players);
      if (data.players !== undefined) {
        scores = data.players;
        display_scores();
      }
    });

    return () => {
      socket.emit("disconnect", name);
      socket.off();
    };
  }, []);

  useEffect(() => {
    //console.log('i am getting called')
    if (time === 0 && count === 0) {
      setEndgame(true);
      setGShow(false);
      submit_data();
      setCount(1);
    }
  }, [time]);

  //STARTS THE GAME
  const StartGame = (msg) => {
    console.log(name);
    console.log("I am getting called");
    currentSocket.emit("start game", name);
  };
  //Submits data when game is over
  const submit_data = () => {
    currentSocket.emit("Words", {
      name: name,
      words: words,
      no_of_players: players.length,
    });
    currentSocket.emit("get_scores", () => {
      console.log("I am getting called");
    });
  };

  const total_score = (pwords) => {
    if (pwords !== undefined) {
      if (pwords.length !== 0) {
        var score = 0;
        pwords.map((word) => {
          score += word.score;
        });
        return (
          <div>
            {" "}
            <h6> total score : {score}</h6>{" "}
          </div>
        );
      }
    }
  };

  const popover = () => {
   return <Popover id="popover-basic">
      <Popover.Title as="h3">Popover right</Popover.Title>
      <Popover.Content>
        And here's some <strong>amazing</strong> content. It's very engaging.
        right?
      </Popover.Content>
    </Popover>
  }
  


  //DISPLAYS SCORES
  const display_scores = () => {
    if (gridshow === false) {
      return (
        <div>
          {" "}
          <Grid container className={classes.root} spacing={2}>
            <Grid item xs={12}>
              <Grid
                container
                direction="column"
                justify="flex-start"
                alignItems="flex-start"
                spacing={4}
              >
                {" "}
                <ul>
                  {scores.map((player) => (
                    <li>
                        <OverlayTrigger trigger="click" rootCLose placement="bottom" overlay={<Popover id="popover-basic">
      <Popover.Title as="h3">Word List</Popover.Title>
      <Popover.Content>
       
  <Grid
  container
  direction="column"
  justify="center"
  alignItems="center"
  spacing={2}
>
  {player.words.map((word) => (
    <Grid key={word.word} item>
      <h6>
        {word.word !== "" ? `${word.word} - ${word.score}` : ""}
      </h6>
    </Grid>
  ))}
  <h6>{total_score(player.words)}</h6>
  <br/>
  (tap note to show and hide list)
</Grid>

      </Popover.Content>
    </Popover>}>
                      <a>
                      <ThemeProvider theme={theme}>
                        <Typography variant="h3">{player.name}</Typography>
                        <Typography variant="h3">{total_score(player.words)}</Typography>
                      </ThemeProvider>
                      <p>(tap to show and hide word list)</p>
                      </a>
                      </OverlayTrigger>
                    </li>
                  ))}
                </ul>
              </Grid>
            </Grid>
          </Grid>
        </div>
      );
    }
  };

  //RENDER FUNCTIONS
  const display_grid = () => {
    //console.log(grid[0])
    if (gridshow === true) {
      return (
        <div>
          {" "}
          <Grid container className={classes.root} spacing={2}>
            <Grid item xs={12}>
              <Grid container justify="center" spacing={4}>
                {[0, 1, 2, 3].map((value) => (
                  <Grid key={value} item xs={3}>
                    <Paper className={classes.paper}>
                      {" "}
                      <ThemeProvider theme={theme}>
                        <Typography variant="h3">{grid[value]}</Typography>
                      </ThemeProvider>
                    </Paper>
                  </Grid>
                ))}
              </Grid>
            </Grid>
          </Grid>
          <Grid container className={classes.root} spacing={2}>
            <Grid item xs={12}>
              <Grid container justify="center" spacing={4}>
                {[4, 5, 6, 7].map((value) => (
                  <Grid key={value} item xs={3}>
                    <Paper className={classes.paper}>
                      {" "}
                      <ThemeProvider theme={theme}>
                        <Typography variant="h3">{grid[value]}</Typography>
                      </ThemeProvider>
                    </Paper>
                  </Grid>
                ))}
              </Grid>
            </Grid>
          </Grid>
          <Grid container className={classes.root} spacing={2}>
            <Grid item xs={12}>
              <Grid container justify="center" spacing={4}>
                {[8, 9, 10, 11].map((value) => (
                  <Grid key={value} item xs={3}>
                    <Paper className={classes.paper}>
                      {" "}
                      <ThemeProvider theme={theme}>
                        <Typography variant="h3">{grid[value]}</Typography>
                      </ThemeProvider>
                    </Paper>
                  </Grid>
                ))}
              </Grid>
            </Grid>
          </Grid>
          <Grid container className={classes.root} spacing={2}>
            <Grid item xs={12}>
              <Grid container justify="center" spacing={4}>
                {[12, 13, 14, 15].map((value) => (
                  <Grid key={value} item xs={3}>
                    <Paper className={classes.paper}>
                      {" "}
                      <ThemeProvider theme={theme}>
                        <Typography variant="h3">{grid[value]}</Typography>
                      </ThemeProvider>
                    </Paper>
                  </Grid>
                ))}
              </Grid>
            </Grid>
          </Grid>
        </div>
      );
    }
  };
  const render_timer = () => {
    if (gridshow) {
      return (
        <div>
          <h3>..Find Words!!</h3>
          <h4>Seconds Left: {time}</h4>
        </div>
      );
    } else {
      return;
    }
  };

  const text_area = () => {
    return (
      <Form>
        <Form.Group>
          <div className="text-container">
            <textarea
              className="my_text_area"
              onChange={(e) => {
                setWords(e.target.value);
              }}
              rows="20"
              cols="20"
            ></textarea>
          </div>
        </Form.Group>
      </Form>
    );
  };

  const show_text = () => {
    if (gridshow === true) {
      return (
        <div>
          <div id="paper">
            <div id="pattern">
              <div id="content">
                Enter your Words Here
                <br />
                {text_area()}
                <br />
                <br />
                <br />
                <br />
                <br />
                <br />
                <br />
                <br />
                <br />
                <br />
                <br />
                <br />
                <br />
                <br />
                <br />
                <br />
                (Words will automatically submit)
              </div>
            </div>
          </div>
        </div>
      );
    }
  };



  return (
    <Container fixed>
      <div></div>
      <Grid container spacing={2}>
        <Grid item xs={12}>
          <Grid container justify="center" spacing={4}>
            <Grid item>
              <h1>Code: {room}</h1>
            </Grid>
          </Grid>
        </Grid>

        <Grid item xs={12}>
          <Grid
            container
            direction="row-reverse"
            justify="center"
            alignItems="center"
            spacing={3}
          >
            <Grid item>
              <SpacingGrid players={players}></SpacingGrid>
            </Grid>
          </Grid>
        </Grid>

        <Grid item xs={12}>
          <Grid
            container
            direction="row-reverse"
            justify="center"
            alignItems="center"
            spacing={0}
          >
            <Grid item>
              <Button
                onClick={(e) => {
                  StartGame(e);
                }}
              >
                {endgame === true ? "play again" : "start game"}
              </Button>
            </Grid>
          </Grid>
        </Grid>

        <Grid item xs={12}>
          <Grid
            container
            direction="row-reverse"
            justify="center"
            alignItems="center"
            spacing={0}
          >
            <Grid item>{render_timer()}</Grid>
          </Grid>
        </Grid>

        <Grid item sm={8} xs={12}>
          <Grid
            container
            direction="row-reverse"
            justify="space-evenly"
            alignItems="center"
            spacing={0}
          >
            <Grid item>{display_grid()}</Grid>
          </Grid>
        </Grid>

        <Grid item sm={4} xs={12}>
          <Grid
            container
            direction="row-reverse"
            justify="space-evenly"
            alignItems="center"
            spacing={0}
          >
            <Grid item>{show_text()}</Grid>
          </Grid>
        </Grid>
      </Grid>

      <div>{display_scores()}</div>
    </Container>
  );
};

