import React, { useState, useEffect } from "react";
import queryString from "query-string";
import io from "socket.io-client";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import Figure from "react-bootstrap/Figure";
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import Container from "@material-ui/core/Container";
import Paper from "@material-ui/core/Paper";
import { useTimer } from "use-timer";
import SpacingGrid from "./playerlist.jsx";
import { withRouter } from "react-router-dom";
import { createMuiTheme, ThemeProvider } from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";
import Popover from "react-bootstrap/Popover";
import OverlayTrigger from "react-bootstrap/OverlayTrigger";
import "./lobby.css";

const theme = createMuiTheme();

theme.typography.h3 = {
  fontSize: "1.2rem",
  "@media (min-width:600px)": {
    fontSize: "1.5rem",
  },
  [theme.breakpoints.up("md")]: {
    fontSize: "2rem",
  },
};

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(5),
    [theme.breakpoints.down("xs")]: {
      height: 20,
      width: 5,
      padding: theme.spacing(4),
      textAlign: "center",
    },
    height: 120,
    width: 100,
    textAlign: "center",
  },
  control: {
    padding: theme.spacing(4),
  },
}));

let scores = [];

const Lobby = (props) => {
  let { Time } = queryString.parse(window.location.search);
  //DECLARATIONS OF STATE NECESSARY THINGS
 
  const [name, setName] = useState("");
  const [room, setRoom] = useState("");
  const [currentSocket, setCurrentSocket] = useState(null);
  const [players, setPlayers] = useState([]);
  const [count, setCount] = useState(0);
  const [show, setShow] = useState(true);
  const [host, setHost] = useState("");
  const [grid, setGrid] = useState([]);
  const [gridshow, setGShow] = useState(false);
  const [endgame, setEndgame] = useState(false);
  const [words, setWords] = useState("");
  const ENDPOINT = `https://backend-boggle.herokuapp.com/rooms`;
  const classes = useStyles();
  let score_array = [];
  const ordered_scores = [];
  /////
  let { time, start, pause, reset, isRunning } = useTimer({
    initialTime: 10,
    timerType: "DECREMENTAL",
  });

  useTimer.ini

  //Use Effect renders
  useEffect(() => {
    let { name, room } = queryString.parse(window.location.search);
    console.log(queryString.parse(window.location.search));
    setName(name);
    setRoom(room);

   

    const socket = io(`${ENDPOINT}/${room}`);
    setCurrentSocket(socket);

    socket.emit("add_player", name);

    socket.on("add_to_list", (players_list) => {
      console.log(players_list);
      setPlayers(players_list);
    });

    socket.on("host", (host) => {
      console.log("getting called");
      console.log(host);
      setHost(host);
    });

    socket.on("starting_game", (data) => {
      console.log(data.grid[0]);
      setGrid(data.grid);
      setGShow(data.start);
      console.log(gridshow);
      if (data.start) {
        time = 100;
        reset();
        start();
        setCount(0);
      }
    });

    socket.on("sending scores", (data) => {
     
      //console.log('I am getting called')
      console.log(data.players);
      if (data.players !== undefined) {
        scores = data.players;
      }
      order_scores(scores);
    });

    return () => {
      socket.emit("disconnect", name);
      socket.off();
    };
  }, []);

  useEffect(() => {
    //console.log('i am getting called')
    if (time === 0 && count === 0) {
      setEndgame(true);
      setGShow(false);
      submit_data();
      setCount(1);
    }
  }, [time]);

  //STARTS THE GAME
  const StartGame = (msg) => {
    console.log(name);
    console.log("I am getting called");
    currentSocket.emit("start game", name);
  };
  //Submits data when game is over
  const submit_data = () => {
    currentSocket.emit("Words", {
      name: name,
      words: words,
      no_of_players: players.length,
    });
    currentSocket.emit("get_scores", () => {
      console.log("I am getting called");
    });
  };

  const order_scores = () => {
    for (let i = 0; i < scores.length; i++) {
      total_score(scores[i].words);
    }
    score_array.sort(function (a, b) {
      return b - a;
    });
    console.log(score_array);
  };

  const total_score = (pwords) => {
    if (pwords !== undefined) {
      if (pwords.length !== 0) {
        var score = 0;
        pwords.map((word) => {
          score += word.score;
        });
        score_array.push(score);
        return (
          <div>
            {" "}
            <h6> total score : {score}</h6>{" "}
            {score === score_array[0] ? (
              <div>
                <img src="https://img.icons8.com/ios-filled/100/000000/medal2.png" />
              </div>
            ) : (
              ""
            )}
            {score === score_array[1] && score !== score_array[0] ? (
              <img
                src="https://img.icons8.com/ios-filled/100/000000/medal-second-place.png"
                alt=""
              />
            ) : (
              ""
            )}
            {score === score_array[2] && score !== score_array[1] ? (
               <img
               src="https://img.icons8.com/ios-filled/100/000000/medal-third-place.png"
               alt=""
             />
            ) : (
              ""
            )}
          </div>
        );
      }
    }
  };

  const leaderboard = () => {
    return (
      <ThemeProvider theme={theme}>
        <Typography variant="h3">
       {scores.length > 0 ? <strong><h3>Leaderboard</h3> </strong>  : ""}
        </Typography>
      </ThemeProvider>
    );
  };

  //DISPLAYS SCORES
  const display_scores = () => {
    if (gridshow === false) {
      return (
        <div>
          <Grid container>
            <Grid item xs={12}>
              <Grid
                container
                direction="row"
                justify="space-evenly"
                alignItems="center"
                spacing={4}
              >
                {scores.map((player) => (
                  <li>
                    <OverlayTrigger
                      trigger="focus"
                      trigger="hover"
                      placement="bottom"
                      overlay={
                        <Popover id="popover-basic">
                          <Popover.Title as="h3">Word List</Popover.Title>
                          <Popover.Content>
                            <Grid
                              container
                              direction="column"
                              justify="center"
                              alignItems="center"
                              spacing={0}
                            >
                              {player.words.map((word) => (
                                <Grid key={word.word} item>
                                  <h6>
                                    {word.word !== ""
                                      ? `${word.word} - ${word.score}`
                                      : ""}
                                  </h6>
                                </Grid>
                              ))}
                              (tap note to show and hide list)
                            </Grid>
                          </Popover.Content>
                        </Popover>
                      }
                    >
                      <a>
                        <ThemeProvider theme={theme}>
                          <Typography variant="h3">{player.name}</Typography>
                        </ThemeProvider>
                        <h3>{total_score(player.words)}</h3>
                      </a>
                    </OverlayTrigger>
                  </li>
                ))}
              </Grid>
            </Grid>
          </Grid>
        </div>
      );
    }
  };

  //RENDER FUNCTIONS
  const display_grid = () => {
    //console.log(grid[0])
    if (gridshow === true) {
      return (
        <div>
          {" "}
          <Grid container className={classes.root} spacing={2}>
            <Grid item xs={12}>
              <Grid container justify="center" spacing={4}>
                {[0, 1, 2, 3].map((value) => (
                  <Grid key={value} item xs={3}>
                    <Paper className={classes.paper}>
                      {" "}
                      <ThemeProvider theme={theme}>
                        <Typography variant="h3">{grid[value]}</Typography>
                      </ThemeProvider>
                    </Paper>
                  </Grid>
                ))}
              </Grid>
            </Grid>
          </Grid>
          <Grid container className={classes.root} spacing={2}>
            <Grid item xs={12}>
              <Grid container justify="center" spacing={4}>
                {[4, 5, 6, 7].map((value) => (
                  <Grid key={value} item xs={3}>
                    <Paper className={classes.paper}>
                      {" "}
                      <ThemeProvider theme={theme}>
                        <Typography variant="h3">{grid[value]}</Typography>
                      </ThemeProvider>
                    </Paper>
                  </Grid>
                ))}
              </Grid>
            </Grid>
          </Grid>
          <Grid container className={classes.root} spacing={2}>
            <Grid item xs={12}>
              <Grid container justify="center" spacing={4}>
                {[8, 9, 10, 11].map((value) => (
                  <Grid key={value} item xs={3}>
                    <Paper className={classes.paper}>
                      {" "}
                      <ThemeProvider theme={theme}>
                        <Typography variant="h3">{grid[value]}</Typography>
                      </ThemeProvider>
                    </Paper>
                  </Grid>
                ))}
              </Grid>
            </Grid>
          </Grid>
          <Grid container className={classes.root} spacing={2}>
            <Grid item xs={12}>
              <Grid container justify="center" spacing={4}>
                {[12, 13, 14, 15].map((value) => (
                  <Grid key={value} item xs={3}>
                    <Paper className={classes.paper}>
                      {" "}
                      <ThemeProvider theme={theme}>
                        <Typography variant="h3">{grid[value]}</Typography>
                      </ThemeProvider>
                    </Paper>
                  </Grid>
                ))}
              </Grid>
            </Grid>
          </Grid>
        </div>
      );
    }
  };
  const render_timer = () => {
    if (gridshow) {
      return (
         <ThemeProvider theme={theme}>
         <Typography variant="h3">Find Words!! Seconds Left: {time}</Typography>
       </ThemeProvider>
      );
    } else {
      return;
    }
  };

  const text_area = () => {
    return (
      <Form>
        <Form.Group>
          <div className="text-container">
            <textarea
              className="my_text_area"
              onChange={(e) => {
                setWords(e.target.value);
              }}
              placeholder="enter your words here"
              rows="20"
              cols="20"
            ></textarea>
          </div>
        </Form.Group>
      </Form>
    );
  };

  const show_text = () => {
    if (gridshow === true) {
      return (
        <div>
          <div id="paper">
            <div id="pattern">
              <div id="content">
                {text_area()}
                <br />
                <br />
                <br />
                <br />
                <br />
                <br />
                <br />
                <br />
                <br />
                <br />
                <br />
                <br />
                <br />
                <br />
                <br />
                <br />
                (Words will automatically submit)
              </div>
            </div>
          </div>
        </div>
      );
    }
  };

  const render_code = () => {
    if (!gridshow && scores.length === 0) {
      return (
        <div>
          <ThemeProvider theme={theme}>
            <Typography variant="h3">
              <strong>
                {" "}
                {`${host}'s room`} Code: {room}
              </strong>
            </Typography>
          </ThemeProvider>
        </div>
      );
    }
    return <div>code:{room} {leaderboard()}</div>
  };

  return (
    <Container fixed>
     
        <Grid item xs={12}>
          <Grid container justify="center" spacing={4}>
            <Grid item>{render_code()}</Grid>
          </Grid>
        </Grid>

        <Grid>{display_scores()}
        <br/></Grid>
        
        <Grid container>
        <Grid item xs={12}>
          <Grid
            container
            direction="row-reverse"
            justify="center"
            alignItems="center"
            spacing={3}
          >
            <Grid item>
              <SpacingGrid players={players} gridshow={gridshow} scores={scores} ></SpacingGrid>
            </Grid>
          </Grid>
        </Grid>
        
        <Grid item xs={12}>
          <Grid
            container
            direction="row-reverse"
            justify="center"
            alignItems="center"
            spacing={0}
          >
            <Grid item>
              {gridshow === false? (
                <Button
                  onClick={(e) => {
                    StartGame(e);
                  }}
                >
                  {endgame === true ? "play again" : "start game"}
                </Button>
              ) : (
                ""
              )}
            </Grid>
          </Grid>
        </Grid>

        <Grid item xs={12}>
          <Grid
            container
            direction="row-reverse"
            justify="center"
            alignItems="center"
            spacing={0}
          >
            <Grid item>{render_timer()}</Grid>
          </Grid>
        </Grid>

        <Grid item sm={8} xs={12}>
          <Grid
            container
            direction="row-reverse"
            justify="space-evenly"
            alignItems="center"
            spacing={0}
          >
            <Grid item>{display_grid()}</Grid>
          </Grid>
        </Grid>

        <Grid item sm={4} xs={12}>
          <Grid
            container
            direction="row-reverse"
            justify="space-evenly"
            alignItems="center"
            spacing={0}
          >
            <Grid item>{show_text()}</Grid>
          </Grid>
        </Grid>

      </Grid>

    </Container>
  );
};

export default withRouter(Lobby);

