                <Grid container justify="center"
                   >
                       <h6>Players</h6>
                    <Grid item container
                        direction="row"
                        justify="space-evenly"
                        alignItems="center">
                        {props.players.map((player, index) => (
                            <Grid key={index} item>
                                <div>{player.name}</div>
                            </Grid>
                        ))}
                    </Grid>
                </Grid>
            </Paper>
        </Grid>
    );
}
